mapper.update:
UPDATE MENU_OPTION
SET 
	CATEGORY_NO = #{categoryNo},
	OPTION_NAME = #{optionName},
	OPTION_PRICE = #{optionPrice}
WHERE OPTION_NO = #{optionNo}

mapper.select:
SELECT o.ORDER_NO as orderNo, o.TABLE_NO as tableNo, m.MENU_NAME as menuName, od.AMOUNT as totalAmount, NVL2(mo.OPTION_NAME, mo.OPTION_NAME, '-') as optionName, od.PRICE as price, TO_CHAR(o.ORDER_DATE, 'YYYY-MM-DD HH24:MI:SS') as orderDate FROM ORDERS o JOIN ORDERS_DETAIL od ON o.ORDER_NO = od.ORDER_NO JOIN MENU m ON od.MENU_NO = m.MENU_NO LEFT JOIN MENU_OPTION mo ON od.OPTION_NO = mo.OPTION_NO WHERE 1=1
AND TRUNC(o.ORDER_DATE) BETWEEN TO_DATE(#{startDate}, 'YYYY-MM-DD') AND TO_DATE(#{endDate}, 'YYYY-MM-DD')
AND o.ORDER_NO = #{searchTerm}
AND o.TABLE_NO = #{searchTerm}
AND m.MENU_NAME LIKE '%' || #{searchTerm} || '%'
ORDER BY o.ORDER_DATE DESC, o.ORDER_NO
